using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace Enigma
{
    internal class Challenge10Scans
    {

        public static void RotorsAndKEY()
        {
            Cipher.messageText = "";

            string rings = "AAA";
            Cipher.minIoC = 4100;         

            EnigmaMachine.EnigmaSetup(null, null, null, rings);

        }

        public static void RotorRAndRingR()
        {
            Cipher.messageText = "";

            Cipher.blIoCDiff = true;

            GetData.GetDataXmlFile = "RotorsAndKEY.xml";
            GetData.GetDataTableName = "dtRotorsAndKEY";

            GetData.GetDataRotorRAndRingR();
        }

        public static void RotorMAndRingM()
        {
            Cipher.messageText = "";

            Cipher.blIoCDiff = true;

            GetData.GetDataXmlFile = "RotorRAndRingR.xml";
            GetData.GetDataTableName = "dtRotorRAndRingR";

            GetData.GetDataRotorMAndRingM();
        }

        public static void Plugs(int intPlugnumber)
        {
            Cipher.messageText = "";

            Cipher.blIoCDiff = true;

            if(intPlugnumber == 1)
            {
                GetData.GetDataXmlFile = "RotorMAndRingM.xml";
                GetData.GetDataTableName = "dtRotorMAndRingM";
            }
            else
            {
                string tmpXmlFileName = "Plug" + Convert.ToString(intPlugnumber - 1);

                GetData.GetDataXmlFile = tmpXmlFileName + ".xml";
                GetData.GetDataTableName = "dtPlugs";

            }

            GetData.GetDataPlugs(intPlugnumber);

        }

        public static void KEYs()
        {
            Cipher.blCompare = true;
            Cipher.compare = "GXT";
            Cipher.scanNumber = 5;
            Cipher.messageText = "EDF";

            EnigmaMachine.SetPlugs(1, 10, null, null, null, null);
        }

    }
}
